@php
    $height = '2.5em';
    if ($icon == 'tiktok') {
        $height = '3em';
    }
    $link = route('get-code', [
        'code' => auth()->user()->referral_code,    
    ]);

    if ($icon == 'facebook') {
        $link = config('laravel-share.services.facebook.uri') . $link;
    } elseif ($icon == 'twitter') {
        $link = config('laravel-share.services.twitter.uri') . '?text=' . config('laravel-share.services.twitter.text') . ' ' . $link;
    } elseif ($icon == 'linkedin') {
        $link = config('laravel-share.services.linkedin.uri') . '?mini=' . config('laravel-share.services.linkedin.extra.mini') . '&url=' . $link;
    } else {
        $link = '';
    }
@endphp

